class MovieResponse {
  MovieResponse({
    int? page,
    List<MovieResultsResponse>? results,
    int? totalPages,
    int? totalResults,
  }) {
    _page = page;
    _results = results;
    _totalPages = totalPages;
    _totalResults = totalResults;
  }

  MovieResponse.fromJson(dynamic json) {
    _page = json['page'];
    if (json['results'] != null) {
      _results = [];
      json['results'].forEach((v) {
        _results?.add(MovieResultsResponse.fromJson(v));
      });
    }
    _totalPages = json['total_pages'];
    _totalResults = json['total_results'];
  }

  int? _page;
  List<MovieResultsResponse>? _results;
  int? _totalPages;
  int? _totalResults;

  MovieResponse copyWith({
    int? page,
    List<MovieResultsResponse>? results,
    int? totalPages,
    int? totalResults,
  }) =>
      MovieResponse(
        page: page ?? _page,
        results: results ?? _results,
        totalPages: totalPages ?? _totalPages,
        totalResults: totalResults ?? _totalResults,
      );

  int? get page => _page;

  List<MovieResultsResponse>? get results => _results;

  int? get totalPages => _totalPages;

  int? get totalResults => _totalResults;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['page'] = _page;
    if (_results != null) {
      map['results'] = _results?.map((v) => v.toJson()).toList();
    }
    map['total_pages'] = _totalPages;
    map['total_results'] = _totalResults;
    return map;
  }
}

